<template>
  <view class="container">
    <view class="statusbar" />

    <view class="efe">
      <button title="Tomar Foto" @press="tomarFoto" color="black"></button>
      <image class="test" :source="{ uri: fotomeme }" />
    </view>

    <view class="efe">
      <button title="Tomar Video" @press="tomarVideo" color="black"></button>
      <video class="test1" :source="{ uri: videomeme }" useNativeControls resizeMode="contain" />
    </view>
  </view>
</template>

<script>
import { Video } from "expo-av";
export default {
  data() {
    return {
      fotomeme:
        "data:image/jpeg;base64,",
      videomeme:
        "http://commondatastorage.googleapis.com/gtv-videos-bucket/sample/BigBuckBunny.mp4"
    };
  },
  props: {
    navigation: {
      type: Object
    }
  },
  methods: {
    tomarFoto() {
      this.navigation.navigate("Camara", {
        onGoBack: this.refresh,
        tipo: "Foto"
      });
    },
    tomarVideo() {
      this.navigation.navigate("Camara", {
        onGoBack: this.refresh,
        tipo: "Video"
      });
    },
    refresh(data, tipo) {
      tipo == "Foto" ? (this.fotomeme = data) : (this.videomeme = data);
    }
  },
  components: { Video }
};
</script>

